import React, {useState, useImperativeHandle, forwardRef, createRef, useMemo} from 'react';
import styles from './style.module.scss';
import {Button} from "antd";
import {AreaItem} from "../areaItem";

let refs = [];

export const AreaList = forwardRef((props, ref) => {
    const [children, setChildren] = useState(props.children);
    // 为了每次渲染时候性能高一些 ,不希望它重复的反复的渲染 做一下缓存或者说性能优化 用了 useMemo
    useMemo(() => {
        refs = children.map((item) => createRef());
    }, [children])
    // 增加
    const addItemToChildren = () => {
        const newChildren = [...children];
        newChildren.push({});
        setChildren(newChildren);
    }
    // 删除 这里给 子组件使用 就是 AreaItem 通过子组件标签 赋值传递
    const removeItemFromChildren = (index) => {
        const newChildren = [...children];
        newChildren.splice(index, 1);
        setChildren(newChildren);
    }
    // ref
    useImperativeHandle(ref, () => {
        return {
            // 给父组件提供的一个方法  当你调用我的时候 我会知道我有几个children 然后去循环 在从每个children每个子组件的时候在去
            // 获取schema 拼接获取给上层的组件 也就是一层一层的传递 这边就是传递给了 HomeManagePage了
            getSchema: () => {
                // 只负责 管理 children这一层 , 你问我下一层组件存的是什么，我也不知道, 所以这边在通过children,调用下一层的
                // getSchema 然后去把对应的内容 返给上一层  只要为了后面好维护, 那一个层级有问题 找那一层级的问题就好了
                const schema = [];
                children.forEach((item, index) => {
                    schema.push(refs[index].current.getSchema());
                })
                return schema;
            },
            resetSchema: ()=>{

            }
        };
    })
    return (
        <div>
            <ul className={styles.list}>
                {children.map((item, index) => (
                    <AreaItem key={index} item={item} index={index}
                              removeItemFromChildren={removeItemFromChildren}
                              ref={refs[index]}
                    />
                ))}
            </ul>
            <Button type='primary' ghost onClick={addItemToChildren}>新增区块按钮</Button>
        </div>
    )
})
