import React from 'react';
import styles from './style.module.scss';
import {Button} from "antd";
import {AreaItem} from "../areaItem";
import {useDispatch, useSelector} from "react-redux";
import {ADD_PAGE_CHILDREN} from "../../redux/constant";


export const AreaList = () => {
    const dispatch = useDispatch();
    const children = useSelector((state) => state.homeManagePageReducer.schema?.children || []);
    // 增加
    const addItemToChildren = () => {
        dispatch({
            type: ADD_PAGE_CHILDREN,
            value:{}
        })
    }


    return (
        <div>
            <ul className={styles.list}>
                    {children.map((item, index) => (
                        <AreaItem key={index} item={item} index={index}/>
                    ))}
            </ul>
            <Button type='primary' ghost onClick={addItemToChildren}>新增区块按钮</Button>
        </div>
    )
}
