import React from 'react';
import styles from './style.module.scss';

export const Footer = ({schema}) => {
    const {attributes = {}, children = []} = schema;
    const {copyright, record} = attributes;
    return (
        <div className='wrapper'>
            <div className={styles.footer}>
                <ul className={styles.list}> {children.map((item, index) => {
                    const {attributes = {}} = item;
                    const {title, description, link, imageUrl} = attributes;
                    return (
                        <li className={styles.item} key={index}>
                            <a className={styles.link} href={link} target='_blank' rel='noreferrer'>
                                <img className={styles.img}
                                     src={imageUrl}
                                     alt={title}/>
                                <h4 className={styles.title}>{title || '暂无标题'}</h4>
                                <p className={styles.desc}>{description || '暂无描述'}</p>
                            </a>
                        </li>
                    )
                })}

                    <li className={styles.item}>
                        <a className={styles.link} href={"/admin.html"}>进入管理页面</a>
                    </li>
                </ul>
                <div className={styles.copyright}>
                    {copyright} {record}
                </div>
            </div>
        </div>
    );
}
