import {CHANGE_SCHEMA} from "./constant";
import {ADD_PAGE_CHILDREN} from "./constant";
import {CHANGE_PAGE_CHILD} from "./constant";

export const getChangeSchemaAction = (schema) => {
    return {
        type: CHANGE_SCHEMA,
        value: schema,
    }
}


export const getAddPageChildrenAction = () => {
    return {
        type: ADD_PAGE_CHILDREN,
        value:{},
    }
}

export const getChangePageChildAction = (value,index) => {
    return {
        type:CHANGE_PAGE_CHILD,
        index,
        value
    }
}
