import React, {useState,useEffect} from 'react';
import ReactDOM from 'react-dom';
import {HashRouter as Router, Switch, Route, Link} from 'react-router-dom';
import {BasicSetting} from "./pages";

import {HomeManagePage} from "./pages";
import {Provider, useDispatch, useSelector} from "react-redux";
import store from "./redux/store";

import './style.scss';
import "normalize.css";
import 'antd/dist/antd.css';

import styles from './style.module.scss';
import {Layout, Menu} from "antd";
import {getChangePageAttributeAction, getChangeSchemaAction} from "./redux/action";

const {Header, Sider, Content} = Layout;
// 自定义的hooks
const useCollapsed = () => {
    const [collapsed, setCollapsed] = useState(false);
    const toggleCollapsed = () => setCollapsed(!collapsed);
    return {collapsed, toggleCollapsed};
}
// 自定义 hooks 把重复的事情 集合起来 schema 传数据 取数据 统一管理
const useStore = () => {
    const dispatch = useDispatch();
    const schema = useSelector((state) => state.homeManagePageReducer.schema);
    const changeSchema = (schema) => {
        dispatch(getChangeSchemaAction(schema));
    }
    const changePageAttribute = (key, value) => {
        dispatch(getChangePageAttributeAction(key, value));
    }
    return {schema, changePageAttribute, changeSchema};
}
const Wrapper = () => {
    const handleHomePage = () => window.location.href = '/';
    const {collapsed, toggleCollapsed} = useCollapsed();
    useEffect(()=> {

    })
    return (
        <Router>
            <Layout>
                <Sider className={styles.sidebar} trigger={null} collapsible collapsed={collapsed}>
                    <Menu theme="dark" mode="inline" defaultSelectedKeys={['adminHome']}>
                        <Menu.Item key='adminHome'>
                            <Link to='/'>
                                <span className='iconfont'>&#xe821;首页内容管理</span>
                            </Link>
                        </Menu.Item>
                        <Menu.Item key='adminSetting'>
                            <Link to='/setting'>
                                <span className='iconfont'>&#xe821;基础内容配置</span>
                            </Link>
                        </Menu.Item>
                        <Menu.Item key='adminBack' onClick={handleHomePage}>
                            <span className='iconfont'>&#xe629;返回用户页面</span>
                        </Menu.Item>
                    </Menu>
                </Sider>
                <Layout>
                    <Header className={styles.header}>
                        {collapsed ?
                            <span className='iconfont' onClick={toggleCollapsed}>&#xe62c;</span>
                            : <span className='iconfont' onClick={toggleCollapsed}>&#xe62a;</span>
                        }
                    </Header>
                    <Content className={styles.content}>
                        <Switch>
                            <Route exact path='/' component={HomeManagePage}></Route>
                            <Route exact path='/setting' component={BasicSetting}></Route>
                            <Route></Route>
                        </Switch>
                    </Content>
                </Layout>
            </Layout>
        </Router>
    )
}


ReactDOM.render(
    <Provider store={store}>
        <Wrapper/>
    </Provider>,
    document.getElementById('root')
);
