        <div>
            <ul className={styles.list}>
                {list.map((item, index) => (
                    <li key={index} className={styles.item}>
                        <span className={styles.content}>当前区块内容为空</span>
                        <span className={styles.delete}>
                            <Button size='small' type='dashed' danger
                                    onClick={() => handleRemoveButtonClick(index)}>删除</Button>
                        </span>
                    </li>
                ))}
            </ul>
            <Button type='primary' ghost onClick={handleAddButtonClick}>新增区块按钮</Button>
        </div>
    )
})
