import React from 'react';
import styles from './style.module.scss';
import {Input, Button} from "antd";

export const AdminList = (props) => {
    const {attributes = {}, changeAttributes} = props;
    const {title, description, showSmallPic, smallPicUrl, backgroundUrl, backgroundHeight} = attributes;

    const handleShowSmallPicChange = (checked) => {
        if (!checked) {
            changeAttributes({
                showSmallPic: checked, smallPicUrl: ''
            });
        } else {
            changeAttributes({showSmallPic: checked});
        }
    }

    return (
        <div className={styles.wrapper}>
            <Button type='primary' className={styles.button}>新增列表项</Button>
            <div className={styles.area}>
                <div className={styles.row}>
                    <span className={styles.label}>标题</span>
                    <Input
                        className={styles.content}
                        value={title}
                        placeholder='请输入标题'
                        onChange={(e) => {
                            changeAttributes({title: e.target.value})
                        }}/>
                </div>
                <div className={styles.row}>
                    <span className={styles.label}>描述</span>
                    <Input
                        className={styles.content}
                        value={title}
                        placeholder='请输入描述'
                        onChange={(e) => {
                            changeAttributes({title: e.target.value})
                        }}/>
                </div>
                <div className={styles.row}>
                    <span className={styles.label}>图片</span>
                    <Input
                        className={styles.content}
                        value={title}
                        placeholder='请输入图片地址'
                        onChange={(e) => {
                            changeAttributes({title: e.target.value})
                        }}/>
                </div>
                <div className={styles.row}>
                    <span className={styles.label}>链接</span>
                    <Input
                        className={styles.content}
                        value={title}
                        placeholder='请输入跳转链接'
                        onChange={(e) => {
                            changeAttributes({title: e.target.value})
                        }}/>
                </div>
            </div>
            <div className={styles.area}>
                <div className={styles.row}>
                    <span className={styles.label}>标题</span>
                    <Input
                        className={styles.content}
                        value={title}
                        placeholder='请输入标题'
                        onChange={(e) => {
                            changeAttributes({title: e.target.value})
                        }}/>
                </div>
                <div className={styles.row}>
                    <span className={styles.label}>描述</span>
                    <Input
                        className={styles.content}
                        value={title}
                        placeholder='请输入描述'
                        onChange={(e) => {
                            changeAttributes({title: e.target.value})
                        }}/>
                </div>
                <div className={styles.row}>
                    <span className={styles.label}>图片</span>
                    <Input
                        className={styles.content}
                        value={title}
                        placeholder='请输入图片地址'
                        onChange={(e) => {
                            changeAttributes({title: e.target.value})
                        }}/>
                </div>
                <div className={styles.row}>
                    <span className={styles.label}>链接</span>
                    <Input
                        className={styles.content}
                        value={title}
                        placeholder='请输入跳转链接'
                        onChange={(e) => {
                            changeAttributes({title: e.target.value})
                        }}/>
                </div>
            </div>
        </div>
    )
}
