import React, {useState} from 'react';
import styles from "./style.module.scss";
import {Button, Modal, Select} from "antd";
import {useDispatch, useSelector} from "react-redux";
import {getChangePageChildAction, getChangeSchemaAction, getDelectPageChildAction} from "../../redux/action";

const {Option} = Select;

// 自定义 hooks 把重复的事情 集合起来 schema 传数据 取数据 统一管理
const useStore = (index) => {
    const dispatch = useDispatch();
    const pageChild = useSelector((state) => state.homeManagePageReducer.schema.children?.[index] || {});
    const changePageChild = (temp) => {dispatch(getChangePageChildAction(index,temp));}
    const removePageChild = () => {dispatch(getRemovePageChildAction(index))}
    return {pageChild,changePageChild,removePageChild};
}

export const AreaItem = (props) => {

    // 取父组件数据 就是 AreaList 组件
    const {index} = props;
    const {pageChild,changePageChild,removePageChild} = useStore(index)

    const [isModalOpen, setIsModalOpen] = useState(false);

    // 控制临时弹窗
    const [temp, setTemp] = useState(pageChild);

    // 确定
    const handleOkClick = () => {
        setIsModalOpen(false);
        changePageChild(temp);
    };

    // 取消
    const handleModalCancelClick = () => {
        setIsModalOpen(false);
        setTemp(pageChild);
    };

    // 展示
    const showModal = () => {
        setIsModalOpen(true);
    };

    // 改变
    const handleSelectChange = (value) => {
        setTemp({name:value,attributes:{},children:[]});
    }

    return (
        <div>
            <li className={styles.item}>
                <span className={styles.content} onClick={showModal}>
                    {pageChild.name ? pageChild.name + '组件' : '当前区块内容为空'}</span>
                <span className={styles.delete}>
                    <Button size='small' type='dashed' danger onClick={removePageChild(index)}>删除</Button>
                </span>
                <Modal title="选择组件" open={isModalOpen} onOk={handleOkClick}
                       onCancel={handleModalCancelClick}>
                    <Select value={temp.name} className={styles.select}
                            onChange={handleSelectChange}>
                        <Option value='Banner'>Banner 组件</Option>
                        <Option value='List'>List 组件</Option>
                        <Option value='Footer'>Footer 组件</Option>
                    </Select>
                </Modal>
            </li>
        </div>
    )
}
