import React,{useState} from 'react';
import styles from './style.module.scss';
import { MenuFoldOutlined, MenuUnfoldOutlined, UserOutlined, VideoCameraOutlined,} from '@ant-design/icons';
import { Layout, Menu } from 'antd';
const { Header, Sider, Content } = Layout;


export const HomeManagePage = () => {
    const [collapsed, setCollapsed] = useState(false);
    // 返回 homePage页面
    const handleHomePage = () => window.location.href = '/';
    return (
        <Layout>
            <Sider trigger={null} collapsible collapsed={collapsed}>
                <div className="logo" />
                <Menu theme="dark" mode="inline" defaultSelectedKeys={['1']}>
                    <Menu.Item key='1'>
                        <span className='iconfont'>&#xe821;首页内容管理</span>
                    </Menu.Item>
                    <Menu.Item key='2' onClick={handleHomePage}>
                        <span className='iconfont'>&#xe629;返回用户页面</span>
                    </Menu.Item>
                </Menu>
            </Sider>
            <Layout className="site-layout">
                <Header className="site-layout-background" style={{padding: 0,}}>
                    {collapsed ?
                        <span className={'iconfont'} onClick={() => setCollapsed(!collapsed)}>&#xe62a;</span>
                        : <span className='iconfont' onClick={() => setCollapsed(!collapsed)}>&#xe62c;</span>
                    }
                </Header>
                <Content
                    className="site-layout-background"
                    style={{
                        margin: '24px 16px',
                        padding: 24,
                        minHeight: 1000,
                    }}
                >
                    Content
                </Content>
            </Layout>
        </Layout>
    );
};
