import React from 'react';
import styles from './style.module.scss';
import {Button, Input} from "antd";

export const Footer = (props) => {
    const {attributes = {}, changeAttributes, children = [], changeChildren} = props;
    const {copyright, record} = attributes;
    // 增加列表项
    const addItemToChildren = () => {
        const newChildren = [...children];
        newChildren.push({
            name: 'Item',
            attributes: {title: '', description: '', imageUrl: '', link: ''},
            children: []
        });
        changeChildren(newChildren);
    }
    // 删除列表项
    const deleteItemFromChildren = (index) => {
        const newChildren = [...children];
        newChildren.splice(index, 1);
        changeChildren(newChildren);
    }

    // 改列表内容
    const changeChildrenItem = (index, key, value) => {
        const originItem = children[index];
        const item = {...originItem};
        item.attributes[key] = value;
        const newChildren = [...children]
        newChildren.splice(index, 1, item);
        changeChildren(newChildren);
    }

    return (
        <div className={styles.wrapper}>
            <div className={styles.row}>
                <span className={styles.label}>版权信息</span>
                <Input
                    className={styles.content}
                    value={copyright}
                    placeholder='请输入页面标题'
                    onChange={(e) => {
                        changeAttributes({copyright: e.target.value})
                    }}/>
            </div>
            <div className={styles.row}>
                <span className={styles.label}>备案信息</span>
                <Input
                    className={styles.content}
                    value={record}
                    placeholder='请输入页面描述'
                    onChange={(e) => {
                        changeAttributes({record: e.target.value})
                    }}/>
            </div>
            <Button type='primary' className={styles.button} onClick={addItemToChildren}>新增列表项</Button>
            {children.map(({attributes: {title, description, imageUrl, link}}, index) =>
                <div className={styles.area} key={index}>
                    <div className={styles.delete} onClick={() => deleteItemFromChildren(index)}>X</div>
                    <div className={styles.row}>
                        <span className={styles.label}>标题</span>
                        <Input
                            className={styles.content}
                            value={title}
                            placeholder='请输入标题'
                            onChange={(e) => {
                                changeChildrenItem(index, 'title', e.target.value)
                            }}
                        />
                    </div>
                    <div className={styles.row}>
                        <span className={styles.label}>描述</span>
                        <Input
                            className={styles.content}
                            value={description}
                            placeholder='请输入描述'
                            onChange={(e) => {
                                changeChildrenItem(index, 'description', e.target.value)
                            }}
                        />
                    </div>
                </div>)}
        </div>
    )
}
