import React from 'react';
import styles from './style.module.scss';


export const Banner = ({schema}) => {
    const {attributes = {}} = schema;
    const {title, description, showSmallPic,smallPicUrl,backgroundUrl,backgroundHeight} = attributes;
    const wrapperStyleObj = backgroundUrl ?{background} : {};
    backgroundHeight && (wrapperStyleObj.height = parseInt(backgroundHeight,10));
    return (
        <div className='wrapper' style={wrapperStyleObj}>
            <div className={styles.banner}>
                <div className={styles.person}>
                    {(showSmallPic && smallPicUrl) ? <img className={styles.avatar}
                                         src='https://serverless-project-yc.oss-cn-hangzhou.aliyuncs.com/images/avatar.JPG'
                                         alt='Mikey'/> : null}
                    <div className={styles.content}>
                        <div className={styles.title}>{title}</div>
                        <div className={styles.description}>{description}</div>
                    </div>
                </div>
            </div>
        </div>
    );
}
