import React from 'react';
import styles from './style.module.scss';

export const Banner = () => {
    const title = window.localStorage.title || "Mikey 的 Blog"
    const description = window.localStorage.description || "从编程到生活,记录我一路上成长的过程."
    return (
        <div className='wrapper'>
            <div className={styles.banner}>
                <div className={styles.person}>
                    <img className={styles.avatar}
                         src='https://serverless-project-yc.oss-cn-hangzhou.aliyuncs.com/images/avatar.JPG'
                         alt='Mikey'/>
                    <div className={styles.title}>{title}</div>
                    <div className={styles.description}>{description}<br/>
                        我的一路成长过程 ...
                    </div>
                </div>
            </div>
        </div>
    );
}
