import {produce} from 'immer';
import {parseJsonByString} from "../../common/utils";
import {CHANGE_SCHEMA,ADD_PAGE_CHILDREN} from "./constant";

const initialSchema = parseJsonByString(window.localStorage.schema, {
    name: 'page',
    attributes: {},
    children: []
});

const defaultState = {
    schema:initialSchema
};

export const homeManagePageReducer = (state = defaultState, action) => produce(state, (draft) => {
    switch (action.type) {
        case CHANGE_SCHEMA:
            draft.schema = action.value;
            break;
        case ADD_PAGE_CHILDREN:
            draft.schema.children.splice()
        default:
            break;
    }
})
