import React, {useState} from 'react';
import styles from "./style.module.scss";
import {Button, Modal, Select} from "antd";

const {Option} = Select;

export const AreaItem = (props) => {
    // 取父组件数据 就是 AreaList 组件
    const {index, removeItemFromChildren, item} = props;
    const [schema,setSchema] = useState(item);
    const [tempSchema,setTempSchema] = useState(item);
    const [isModalOpen, setIsModalOpen] = useState(false);
    const handleOkClick = () => {
        setIsModalOpen(false);
    };

    const handleModalCancelClick = () => {
        setSchema()
        setIsModalOpen(false);
    };
    const showModal = () => {
        setIsModalOpen(true);
    };
    const handleSelectChange = (value) => {
        const schema = {name: value, attributes: {}, children: [],};
        setSchema(schema);
    }

    return (
        <div>
            <li className={styles.item}>
                <span className={styles.content} onClick={showModal}>当前区块内容为空</span>
                <span className={styles.delete}>
                    <Button size='small' type='dashed' danger
                            onClick={() => removeItemFromChildren(index)}>删除
                    </Button>
                </span>
                <Modal title="选择生成的组件" open={isModalOpen} onOk={handleOkClick}
                       onCancel={handleModalCancelClick}>
                    <Select value={tempSchema.name} className={styles.select}
                            onChange={handleSelectChange}>
                        <Option value='Banner'>Banner 组件</Option>
                        <Option value='List'>List 组件</Option>
                        <Option value='Footer'>Footer 组件</Option>
                    </Select>
                </Modal>
            </li>
        </div>
    )
}
