import React, {useRef, useState} from 'react';
import styles from './style.module.scss';
import {Button, Layout, Menu} from 'antd';
import {AreaList} from "../../components";
import {parseJsonByString} from "../../../common/utils";

const {Header, Sider, Content} = Layout;

const schema = parseJsonByString(window.localStorage.schema,{});
// 自定义的hooks
const useCollapsed = () => {
    const [collapsed, setCollapsed] = useState(false);
    const toggleCollapsed = () => setCollapsed(!collapsed);
    return {collapsed, toggleCollapsed};
}

export const HomeManagePage = () => {
    const {collapsed, toggleCollapsed} = useCollapsed();
    // 返回 homePage页面
    const handleHomePage = () => window.location.href = '/';
    const areaListRef = useRef();
    // 保存
    const handleSaveButtonClick = () => {
        const {getSchema} = areaListRef.current;
        //  这边构建的逻辑就是 首先name 拼好 , attributes拼好, children是什么我不知道 通过ref 找到children, 然后调用
        // children的方法 getSchema 意思就是我不知道 children长什么样 你children告诉我就好了 大致中心思路不破坏原有数据的情况下
        // 在复用生成新的下一级 外层的组件只负责自己的name 和 attributes, children就由下层组件来负责就好了
        const schema = {name: "Page", attributes: {}, children:getSchema(),}
        window.localStorage.schema= JSON.stringify(schema);
    }
    // 还原  这里是 当你点击 Reset 方法的时候, 你需要做的只是 调用一下 resetSchema的方法就好了, 至于怎么重置交给子组件来决定
    //  就是交给 AreaList 组件决定
    const handleResetButtonClick = () => {
        const { resetSchema} = areaListRef.current;
        resetSchema();
    }
    return (
        <Layout>
            <Sider className={styles.sidebar} trigger={null} collapsible collapsed={collapsed}>
                <Menu theme="dark" mode="inline" defaultSelectedKeys={['adminHome']}>
                    <Menu.Item key='adminHome'>
                        <span className='iconfont'>&#xe821;首页内容管理</span>
                    </Menu.Item>
                    <Menu.Item key='adminBack' onClick={handleHomePage}>
                        <span className='iconfont'>&#xe629;返回用户页面</span>
                    </Menu.Item>
                </Menu>
            </Sider>
            <Layout>
                <Header className={styles.header}>
                    {collapsed ?
                        <span className='iconfont' onClick={toggleCollapsed}>&#xe62c;</span>
                        : <span className='iconfont' onClick={toggleCollapsed}>&#xe62a;</span>
                    }
                </Header>
                <Content className={styles.content}>
                    <AreaList ref={areaListRef} children={schema.children || []}/>
                    <div className={styles.save}>
                        <Button type='primary' onClick={handleSaveButtonClick}>保存区块配置</Button>
                        <Button type='primary' onClick={handleResetButtonClick}>重置区块配置</Button>
                    </div>
                </Content>
            </Layout>
        </Layout>
    );
};
