import React from 'react';
import styles from './style.module.scss';
import {Input, Button} from "antd";

export const AdminList = (props) => {
    const {children = [], changeChildren} = props;

    // 增加列表项
    const addItemToChildren = () => {
        const newChildren = {...children};
        newChildren.push({});
        changeChildren(newChildren);
    }

    return (
        <div className={styles.wrapper}>
            <Button type='primary' className={styles.button} onClick={addItemToChildren}>新增列表项</Button>
            {children.map((item,index) => <div className={styles.area}>
                <div className={styles.row} key={index}>

                    <span className={styles.label}>标题</span>
                    <Input
                        className={styles.content}
                        //value={title}
                        placeholder='请输入标题'
                        //onChange={(e) => { changeAttributes({title: e.target.value})}}
                    />
                </div>
                <div className={styles.row}>
                    <span className={styles.label}>描述</span>
                    <Input
                        className={styles.content}
                        //value={title}
                        placeholder='请输入描述'
                        //onChange={(e) => {changeAttributes({title: e.target.value})}}
                    />
                </div>
                <div className={styles.row}>
                    <span className={styles.label}>图片</span>
                    <Input
                        className={styles.content}
                        //value={title}
                        placeholder='请输入图片地址'
                        //onChange={(e) => {changeAttributes({title: e.target.value})}}
                    />
                </div>
                <div className={styles.row}>
                    <span className={styles.label}>链接</span>
                    <Input
                        className={styles.content}
                        //value={title}
                        placeholder='请输入跳转链接'
                        //onChange={(e) => {changeAttributes({title: e.target.value})}}
                    />
                </div>
            </div>)}

        </div>
    )
}
