import React,{useState} from 'react';
import styles from "./style.module.scss";
import {Button,Modal} from "antd";

export const AreaItem = (props) => {
    // 取父组件数据 就是 AreaList 组件
    const {index, removeItemFromChildren,item} = props;
    const [isModalOpen, setIsModalOpen] = useState(false);
    const handleOk = () => {
        setIsModalOpen(false);
    };

    const handleCancel = () => {
        setIsModalOpen(false);
    };
    const showModal = () => {
        setIsModalOpen(true);
    };

    return (
        <div>
            <li className={styles.item}>
                <span className={styles.content}>当前区块内容为空</span>
                <span className={styles.delete}>
                    <Button size='small' type='dashed' danger
                            onClick={() => removeItemFromChildren(index)}>删除
                    </Button>
                </span>
                <Modal title="Basic Modal" open={isModalOpen} onOk={handleOk} onCancel={handleCancel}>
                    <p>Some contents...</p>
                    <p>Some contents...</p>
                    <p>Some contents...</p>
                </Modal>
            </li>
        </div>
    )
}
