import React from 'react';
import styles from './style.module.scss';
import {Input} from "antd";

export const Footer = (props) => {
    const {attributes = {}, changeAttributes,children=[],changChildren} = props;
    const {copyright,record} = attributes;
    // 增加列表项
    const addItemToChildren = () => {
        const newChildren = [...children];
        newChildren.push({
            name:'Item',
            attributes:{title: '',description: '',imageUrl: '',link: ''},
            children:[]
        });
        changeChildren(newChildren);
    }
    // 删除列表项
    const deleteItemFromChildren = (index) => {
        const newChildren = [...children];
        newChildren.splice(index,1);
        changeChildren(newChildren);
    }

    return (
        <div className={styles.wrapper}>
            <div className={styles.row}>
                <span className={styles.label}>版权信息</span>
                <Input
                    className={styles.content}
                    value={copyright}
                    placeholder='请输入页面标题'
                    onChange={(e) => {
                        changeAttributes({copyright: e.target.value})
                    }}/>
            </div>
            <div className={styles.row}>
                <span className={styles.label}>备案信息</span>
                <Input
                    className={styles.content}
                    value={record}
                    placeholder='请输入页面描述'
                    onChange={(e) => {
                        changeAttributes({record: e.target.value})
                    }}/>
            </div>
        </div>
    )
}
