import service from "./service";

// 封装get 请求方式
export const get =(url,params) => {
    const config = {
        method: 'get',
        url,
    }
    if (params) {
        config.params = params;
        return service(config);
    }
}

// 封装 post 请求方式
export  const post =(url,data) => {
    const config = {
        method: 'post',
        url,
    }
    if (data) {
        config.data = data;
        return service(config);
    }
}
