import React, {useState, forwardRef, useImperativeHandle} from 'react';
import styles from "./style.module.scss";
import {Button, Modal, Select} from "antd";

const {Option} = Select;
// 定义在外部是因为 在函数内部定义变量 每次函数执行的时候都会重新生成这个变量 性能上不好 它本来也会页面的 render 没关系
// 用 useState 存储也不合适 也会降低性能 所以把它先放在外层 schema原始值
let prevSchema = {};

export const AreaItem = forwardRef((props, ref) => {
    // 取父组件数据 就是 AreaList 组件
    const {index, removeItemFromChildren, item} = props;
    const [schema, setSchema] = useState(item);
    const [isModalOpen, setIsModalOpen] = useState(false);
    // 控制临时弹窗
    const [temp,setTemp] = useState(item);
    // ref
    useImperativeHandle(ref, () => {
        return {
            getSchema: () => {return schema;},
            // 首先把自己的 schema 变成初始的 schema 也就是 setSchema(item)
            resetSchema: () => {
                setSchema(item);
                // 在处理 prevSchema
                prevSchema={};
            }
        };
    })
    // 确定
    const handleOkClick = () => {
        setIsModalOpen(false);
        prevSchema = {};
    };
    // 取消
    const handleModalCancelClick = () => {
        setSchema(prevSchema);
        setIsModalOpen(false);
        prevSchema = {};
    };
    // 展示
    const showModal = () => {
        setIsModalOpen(true);
    };
    // 改变
    const handleSelectChange = (value) => {
        const newSchema = {name: value, attributes: {}, children: [],};
        setTemp(newSchema);
    }

    return (
        <div>
            <li className={styles.item}>
                <span className={styles.content} onClick={showModal}>
                    {schema.name ? schema.name + '组件' : '当前区块内容为空'}</span>
                <span className={styles.delete}>
                    <Button size='small' type='dashed' danger
                            onClick={() => removeItemFromChildren(index)}>删除
                    </Button>
                </span>
                <Modal title="选择组件" open={isModalOpen} onOk={handleOkClick}
                       onCancel={handleModalCancelClick}>
                    <Select value={temp.name} className={styles.select}
                            onChange={handleSelectChange}>
                        <Option value='Banner'>Banner 组件</Option>
                        <Option value='List'>List 组件</Option>
                        <Option value='Footer'>Footer 组件</Option>
                    </Select>
                </Modal>
            </li>
        </div>
    )
})
