import React from 'react';
import styles from './style.module.scss';

export const Banner = () => {
    return (
        <div className={styles.banner}>
            <div className={styles.person}>
                <img className={styles.avatar}
                     src='https://serverless-project-yc.oss-cn-hangzhou.aliyuncs.com/images/avatar.JPG' alt='Mikey'/>
                <div className={styles.title}>Mikey 的个人小站</div>
                <div className={styles.description}>从编程到生活,分享每天的点滴生活,欢迎在这里关注我的动态.<br/>
                    我的一路成长过程 ...
                </div>
            </div>
        </div>
    );
}
